/** Asserts that the given predicate is true, otherwise throws an error. */
export function assert(predicate, message) {
    if (!predicate) {
        throw new Error(`bug in @n1xyz/proton, please report! message: ${message}`);
    }
}
/** Asserts that the given object has the given field using `Object.hasOwn`. */
export function assertHasOwn(x, k) {
    assert(Object.hasOwn(x, k), `expected ${String(k)} to be defined on ${x}`);
}
/** Asserts that the given object is an instance of the given class. */
export function assertInstanceOf(x, cls) {
    assert(x instanceof cls, `expected ${x} to be an instance of ${cls.name}`);
}
